import {useState} from "react";

export default function Word({word})
{
    const [isShow, setIsShow] = useState(false);

    return(
        <tr>
            <td>
                <input type="checkbox" />
            </td>
            <td>
                {word.eng}
            </td>
            <td>
                {isShow && word.kor}
            </td>
            <td>
                <button>뜻 보기</button>
                <button className="btn_del">삭제</button>
            </td>
        </tr>
    )
}
