{
    const [name, setName] = useState('Mike');
    const [age, setAge] = useState(props.age);
    const msg = age > 19 ? "성인 입니다." : "미성년자 입니다.";

    console.log(props);

    return (
        <div>
            <h1>state</h1>
            <h2>{name} ({age} 세) : {msg}</h2>
            <UserName name={name} />
            <button onClick={
                ()=>{
                    setName(name === "Mike"? "Jane" : "Mike");
                    setAge(age+1);
                }
            }>
                Change
            </button>
        </div>
    )
}
