export default function Day()
{
    const { day } = useParams();
    const [words, setWords] = useState([]);

    //실제 DOM에 반영된 직후 useEffect 호출됨
    useEffect(()=>{
        fetch(`http://localhost:3001/words?day=${day}`)
            .then(res => {
                return res.json()
            })
            .then(data => {
                setWords(data);
